def prices():
    response = requests.get(url=f'https://api.gateio.ws/api/v4/spot/tickers')
    currency_price.append(response.json())

def intToDecimal(qty, decimal):
    return int(qty * int("".join(["1"] + ["0"]*decimal)))

def decimalToInt(price, decimal):
    return price/ int("".join((["1"]+ ["0"]*decimal)))

full_value = []
results = []
def check_balance(address, chain, rpc, chain_id):
    try:

        try:
            web3 = Web3(Web3.HTTPProvider(rpc))
            balance = web3.eth.get_balance(web3.to_checksum_address(address))
        except:
            if chain_id == 10:
                web3 = Web3(Web3.HTTPProvider('https://rpc.ankr.com/optimism'))
                balance = web3.eth.get_balance(web3.to_checksum_address(address))

        humanReadable = web3.from_wei(balance,'ether')

        if chain_id in [1, 10, 42161, 42170]:
            symbol = 'ETH'
        if chain_id == 56:
            symbol = 'BNB'
        if chain_id == 137:
            symbol = 'MATIC'
        if chain_id == 43114:
            symbol = 'AVAX'
        if chain_id == 250:
            symbol = 'FTM'
        if chain_id == 324:
            symbol = 'ETH'
        if chain_id == 7777777:
            symbol = 'ETH'
        if chain_id == 534352:
            symbol = 'ETH'
        if chain_id == 8453:
            symbol = 'ETH'
        if chain_id == 59144:
            symbol = 'ETH'

        # check price of token
        for currency in currency_price[0]:
            if currency['currency_pair'] == f'{symbol}_USDT':
                price = currency['last']

        value = round(Decimal(humanReadable)*Decimal(price), 2)

        if value > 0:
            results.append([chain, f'{round(humanReadable, 5)} {symbol}', f'{round(Decimal(humanReadable)*Decimal(price), 2)} $'])

        full_value.append(value)

    except Exception as error:
        cprint(f'error : {error}', 'yellow')

def check_token_balance(address, chain, rpc, token_address, ABI, symbol, decimal):
    try:

        try:
            web3 = Web3(Web3.HTTPProvider(rpc))
            token = web3.eth.contract(address=web3.to_checksum_address(token_address), abi=ABI)
            token_balance = token.functions.balanceOf(web3.to_checksum_address(address)).call()
        except:
            if chain_id == 10:
                web3 = Web3(Web3.HTTPProvider('https://rpc.ankr.com/optimism'))
                token = web3.eth.contract(address=web3.to_checksum_address(token_address), abi=ABI)
                token_balance = token.functions.balanceOf(web3.to_checksum_address(address)).call()

        try:
            humanReadable = decimalToInt(token_balance, decimal)

            # check price of token
            for currency in currency_price[0]:
                if currency['currency_pair'] == f'{symbol}_USDT':
                    price = currency['last']
            if symbol in ['USDT', 'BUSD', 'DAI', 'USDC']:
                price = 1

            value = round(Decimal(humanReadable)*Decimal(price), 2)

            if value > 0.05:
                results.append([chain, f'{round(humanReadable, 5)} {symbol}', f'{round(Decimal(humanReadable)*Decimal(price), 2)} $'])

            full_value.append(value)
        except:
            pass
    except Exception as error:
        cprint(f'error : {error}', 'yellow')


